import{_ as a,c as i,a as t,o as n}from"./app-DITZCmiV.js";const r={};function o(s,e){return n(),i("div",null,e[0]||(e[0]=[t("p",null,"信息系统是由计算机硬件，网络和通信设备、计算机软件、信息资源、信息用户和规章制度组成的以处理信息流为目的的人机一体化系统。",-1),t("p",null,"信息系统的5 个基本功能：输入、存储、处理、输出和控制。 “以计算机为基础〞要求系统设计者 北须具备计算机及其在信息处理中的应用知识。 统组成部分的能力以及人作为信息使用者的各种行为。 •诺兰模型：信息系统进化的阶段模型。将计算机信息系统的发展道路划分为6个阶段： 1.初始阶段：计算机刚进入企业时只作为办公设备使用，应用非常少。一股仅用于财务部门。 2.传播阶段：企业对计算机有了一定了解，想利用计算机解決工作中的问题，比如进行更多的数据 处理，给管理工作和业务带来便利。会大幅度增加软件投入，盲目投入产生问题，效率低。 3.控制阶段：从整体上控制计算机信息系统的发展，在客观上要求组织协调、解決数据共享问题。 信息系统呈现单点、 分散的特点，系统和资源利用率不高。是计算机管理变为数据管理的关键。",-1),t("ol",{start:"4"},[t("li",null,"集成阶段：在控制的基础上，企业开始重新进行规划设计，建立基础数据库，并建成统一的信息 管理系统。使人、财、物等资源信息能够在企业集成共享，更有效地利用现有的T系统和资源。 5.数据管理阶段：企业高层意识到信息战略的重要，信息成为企业的重要资源，企业的信息化建设 也真正进入到数据处理阶段。 使用统一平台，各部门、各系统基本头现资源整合和信息共享。 6.成熟阶段：信息系统己经可以满足企业各个层次的需求，从简单的事务处理到支持高效管理的决 策。 •企业真正把丁 与管理过程结合起来，将组织内部、外部的资源充分整合和利用。")],-1)]))}const m=a(r,[["render",o],["__file","index.html.vue"]]),l=JSON.parse('{"path":"/note/system-architecture/29r8vpuc/","title":"信息系统基础知识","lang":"en-US","frontmatter":{"title":"信息系统基础知识","createTime":"2024/11/24 19:20:57","permalink":"/note/system-architecture/29r8vpuc/"},"headers":[],"readingTime":{"minutes":2.04,"words":611},"git":{"updatedTime":1732512173000,"contributors":[{"name":"qiqiang","email":"qiqiang@pingpongx.com","commits":4,"avatar":"https://avatars.githubusercontent.com/qiqiang?v=4","url":"https://github.com/qiqiang"},{"name":"Quince","email":"qiqiangvae@foxmail.com","commits":1,"avatar":"https://avatars.githubusercontent.com/Quince?v=4","url":"https://github.com/Quince"}]},"filePathRelative":"notes/note/系统架构师/7.信息系统基础知识.md","bulletin":false}');export{m as comp,l as data};
